Script.script:
acal = acal or {}
acal.config = acal.config or {}
acal.config.bgcolors = { {5,90,156}, {27,117,179}, {0,162,173}, {0,160,64}, {129,183,61}, {240,229,37}, {245,193,14}, {240,144,32}, {230,95,37}, {218,38,41}, {128,51,128}, {50,57,135} }
acal.config.colors = {--acal.config.colors or { current = {{0,255,0},{0,0,0}}, serenade = {{200,200,200},{0,0,0}}, reminder = {{255,255,0},{0,0,0}}, upcoming = {{255,0,0},{0,0,0}}, both = {{255,165,0},{0,0,0}} }
acal.config.months = { {'Sarapin','HELP SARAPIS','Learn more about The Logos!'}, {'Daedalan','HELP DAEDALUS','Learn more about The Guardian of Balance!'}, {'Aeguary','HELP AEGIS','Learn more about The God of War!'}, {'Miraman','HELP MIRAMAR','Learn more about The Goddess of Justice!'}, {'Scarlatan','HELP SCARLATTI','Learn more about The Great Bard!'}, {'Ero','HELP EROS','Learn more about The God of Love!'}, {'Valnuary','HELP VALNURANA','Learn more about The Goddess of Sleep and Dreams!'}, {'Lupar','HELP LUPUS','Learn more about The Wild God of the Beasts!'}, {'Phaestian','HELP PHAESTUS', 'Learn more about The Smith!'}, {'Chronos','HELP AEON','Learn more about The Lord of Time!'}, {'Glacian','HELP GLACIUS','Not much is known about Glacius, unfortunantly!'}, {'Mayan','HELP MAYA','Learn more about The Great Mother!'} }
function acal.msg(msg) cecho('\n<orange>[Achaean Calendar]<reset>: '..msg) end
function acal.saveConfig() local filename = acal.getFilePath() .. 'config.lua' table.save(filename, acal.config) acal.msg('Configuration Saved!') end --function
function acal.loadConfig() local filename = acal.getFilePath() .. 'config.lua' if io.exists(filename) then table.load(filename, acal.config) end acal.msg('Configuration Loaded!') end --function
function acal.showConfig() acal.msg('Color Configuration - Click to change!')
setBold(true) setUnderline(true) echo('\n\nMONTH COLOR CONFIG ') resetFormat() for k,v in pairs(acal.config.months) do local bg = acal.config.bgcolors[k] local fg = acal.getFg(bg[1],bg[2],bg[3]) local m = v[1]
echo('\n ') setBgColor(bg[1],bg[2],bg[3]) setFgColor(fg[1],fg[2],fg[3])
local s1 = math.floor((14-#m)/2) local s2 = 14-#m-s1 echo(string.rep(' ',s1)) echo(m) echo(string.rep(' ',s2)) resetFormat() echo(' ') local txt = 'setBG' local cmd = [[acal.configInstructions("]]..m..[[",true)]] local hnt = 'Change background color for '..m setFgColor(82,219,255) setUnderline(true) echoLink(txt,cmd,hnt,true) resetFormat() end
setBold(true) setUnderline(true) echo('\n\nDATE COLOR CONFIG ') resetFormat() for k,v in pairs(acal.config.colors) do echo('\n') echo(string.rep(" ",10-#k)..k) echo(' ') setBgColor(v[1][1],v[1][2],v[1][3]) setFgColor(v[2][1],v[2][2],v[2][3]) echo('25') resetFormat() echo(' ') local txt = 'setBG' local cmd = [[acal.configInstructions("]]..k:upper() ..[[",true)]] local hnt = 'Change background color for '..k setFgColor(82,219,255) setUnderline(true) echoLink(txt,cmd,hnt,true) resetFormat() echo(' ') local txt = 'setFG' local cmd = [[acal.configInstructions("]]..k:upper() ..[[",false)]] local hnt = 'Change foreground color for '..k setFgColor(82,119,255) setUnderline(true) echoLink(txt,cmd,hnt,true) resetFormat() end
end
function acal.configInstructions(c,t) if t then t='BG' p='background' else t='FG' p='foreground' end acal.msg('What should the '..p..' color be for '..c..'?') echo('\nProvide R G B values, seperated by a space.') clearCmdLine() appendCmdLine('ACAL CONFIG SET'..t..' '..c:upper()..' ') end
function acal.configUpdate(t,s,r,g,b)
for k,v in pairs(acal.config.months) do if v[1]:upper() == s:upper() then acal.config.bgcolors[k] = {r,g,b} acal.showConfig() do return end end end
for k,v in pairs(acal.config.colors) do if k:upper() == s:upper() then if t == 'SETBG' then acal.config.colors[k][1] = {r,g,b} else acal.config.colors[k][2] = {r,g,b} end acal.showConfig() do return end end end
acal.msg('That is not a valid configuartion setting!')
end
